import React,{useState} from 'react';
import '../App.css';
import './Button.css';
import { Link, Route, useHistory, withRouter } from 'react-router-dom';
import './HeroSection.css';
import Datum from './Datum'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button } from './Button';



class HeroSection extends React.Component{
  //const [startDate, setStartDate] = useState(new Date());
  //const [endDate, setEndDate] = useState(new Date());
  constructor(props) {
    super(props);
    this.state = {
      startDate: Date.now(),
      endDate: Date.now()
    };
    const history = useHistory();
  }
  render() {
  return (
    <div className='hero-container'>
         <video src='/videos/video-1m.mp4' autoPlay loop muted />
         <div className="date-container">
      <div className="column"> 
      
        <p>Datum dolaska:</p>
        <DatePicker filterDate={d => {
          return new Date() <d;
              }}
          selected={this.state.startDate}
          selectsStart
          startDate={this.state.startDate}
          endDate={this.state.endDate} // add the endDate to your startDate DatePicker now that it is defined
          onChange={date => this.setState(state => ({
            startDate: date
          }))}
        />
      </div>
      
      <div className="column">
      
      <p>Datum odlaska:</p>
       <DatePicker filterDate={d => {
          return new Date() < this.state.startDate;
              }}
          selected={this.state.endDate}
          selectsEnd
          startDate={this.state.startDate}
          endDate={this.state.endDate}
          minDate={this.state.startDate}
          onChange={date => this.setState(state => ({
            endDate: date
          }))}
        />
      </div>
      <div className="column">
      <Route render={({ history}) => (
    <button
      type='button'
      onClick={() => { this.props.history.push('/rezervacija', {
        startDate:this.state.startDate, endDate:this.state.endDate, proba:"Amina"
      }) }}
    >
      Rezerviši sada
    </button>
  )} />
  {/*}
      <Link to={{
            pathname: `/rezervacija`,
            state: {}
          }} className='btn-mobile'>
        <button
          className={`btn btn--outline btn--medium`}
        >
          Rezerviši sada
        </button>
        </Link>*/}
      
      </div>
      
      </div>
      
    </div>
    
  );
            }
}

export default withRouter(HeroSection);
