import React, { useState } from 'react'
import FlavorForm from '../../components/Forma'
import Kalendar from '../../components/Kalendar'
import Datum2 from '../../components/Datum2'
import Button from 'react-bootstrap/Button';
import '../../components/pages/Rezervacija.css'
import { Switch, Route, Link ,Router} from 'react-router-dom';

import 'react-bootstrap'
import { Fragment } from 'react'


export default (props) => {
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const datumiPromijenjeni = (startDate, endDate) => {
        setStartDate(startDate);
        setEndDate(endDate);
    }
  return (
    <Fragment>
    
    <div className="step-one-container">
        <div className="row-s1">
                <Datum2 handle1={(value1)=>{
               //console.log("your value -->",value);
                setStartDate(value1);
             }}  
             handle2={(value1)=>{
                //console.log("your value -->",value);
                 setEndDate(value1);
              }}/>
        </div>
        
        <div className="pick">
        
            <div className="columnPick">
            <i class="fas fa-bed"></i>
                <p>Broj soba:</p>
                <FlavorForm/>
            </div>
            <div className="columnPick">
            <i class="far fa-user"></i>
                <p>Broj odraslih:</p>
                <FlavorForm/>
            </div>
            <div className="columnPick">
            <i class="fas fa-baby"></i>
                <p>Broj djece:</p>
                <FlavorForm/>
            </div>
            
        </div>
    </div>
    <div>
        
    <div className="btn-nastavak">
      <Link exact to={`/rezervacija/${props.korak+1}`}>
      <Button variant="outlined" size="large" 
        style={props.nextStyle}
        onClick={props.next}>
        
        Nastavi rezervaciju
        
      </Button>
      </Link>
    </div>
  </div>
    </Fragment>
    
  )
}
