import React, { useState, Component } from 'react'
import FlavorForm from '../../components/Forma'
import Kalendar from '../../components/Kalendar'
import Datum2 from '../../components/Datum2'
import Button from 'react-bootstrap/Button';
import '../../components/pages/Rezervacija.css'
import { Switch, Route, Link ,Router} from 'react-router-dom';

import 'react-bootstrap'
import { Fragment } from 'react'


export default class StepOne extends Component{
    constructor(props) {
        super(props);
        this.state = {
          startDate: props.startDate,
          endDate: props.endDate,
          brOdraslih: 1,
          brDjece:1,
          brSoba:1,
          next:props.next,
          nextStyle:props.nextStyle,
          korak:props.korak
        };
      }
  render(){
  return (
    <Fragment>
    
    <div className="step-one-container">
        <div className="row-s1">
                <Datum2 handle1={(value1)=>{
               //console.log("your value -->",value);
               this.setState(state => ({
                ...state,
                startDate:value1,
              }))
             }}  
             handle2={(value1)=>{
                //console.log("your value -->",value);
                this.setState(state => ({
                    ...state,
                    endDate:value1,
                  }))
              }}/>
        </div>
        
        <div className="pick">
        {this.props.proba}
            <div className="columnPick">
            <i class="fas fa-bed"></i>
                <p>Broj soba:</p>
                <FlavorForm/>
            </div>
            <div className="columnPick">
            <i class="far fa-user"></i>
                <p>Broj odraslih:</p>
                <FlavorForm/>
            </div>
            <div className="columnPick">
            <i class="fas fa-baby"></i>
                <p>Broj djece:</p>
                <FlavorForm/>
            </div>
            
        </div>
    </div>
    <div>
        
    <div className="btn-nastavak">
      <Link exact to={`/rezervacija/${this.state.korak+1}`}>
      <Button variant="outlined" size="large" 
        style={this.state.nextStyle}
        onClick={this.state.next}>
        
        {this.state.endDate}
        
      </Button>
      </Link>
    </div>
  </div>
    </Fragment>
    
  )
            }
}
