import React, { useState, Component } from 'react'
import FlavorForm from '../../components/Forma'
import Datum2 from '../../components/Datum2'
import Button from 'react-bootstrap/Button';
import '../../components/pages/Rezervacija.css'
import { Switch, Route, Link ,Router, useHistory, withRouter} from 'react-router-dom';

import 'react-bootstrap'
import { Fragment } from 'react'
import StepTwo from './StepTwo'
const prevStyle = {'background': '#33c3f0', 'border-width': '2px'}
const nextStyle = {'background': '#33c3f0',  'border-width': '2px'}
class StepOne extends Component{
    constructor(props) {
        super(props);
        this.state = {
          startDate: props.location.state.startDate,
          endDate: props.location.state.endDate,
          brOdraslih: 1,
          brDjece:1,
          brSoba:1,
          korak:1,
          proba: props.location.state.proba
        };
        console.log(props);
      }
      postaviKorak(indx) {
        this.setState(state => ({
          ...state,
          korak:indx,
        }))
      }
      sljKorak(){ this.postaviKorak(this.state.korak + 1) }
      prethKorak(){ this.postaviKorak(this.state.korak > 0 ? this.state.korak -1 : this.state.korak)}
  render(){
  return (
    <div className="multiStepContainer">
            
    <div className='title-rezerv'>{this.state.proba}</div>
    <div className="multistep-position">
    <Fragment>
    
    <div className="step-one-container">
        <div className="row-s1">
                <Datum2 handle1={(value1)=>{
               //console.log("your value -->",value);
               this.setState(state => ({
                ...state,
                startDate:value1,
              }))
             }}  
             handle2={(value1)=>{
                //console.log("your value -->",value);
                this.setState(state => ({
                    ...state,
                    endDate:value1,
                  }))
              }}/>
        </div>
        
        <div className="pick">
        
            <div className="columnPick">
            <i class="fas fa-bed"></i>
                <p>Broj soba:</p>
                <FlavorForm/>
            </div>
            <div className="columnPick">
            <i class="far fa-user"></i>
                <p>Broj odraslih:</p>
                <FlavorForm/>
            </div>
            <div className="columnPick">
            <i class="fas fa-baby"></i>
                <p>Broj djece:</p>
                <FlavorForm/>
            </div>
            
        </div>
    </div>
    <div>
        
    <div className="btn-nastavak">
      <Link exact to={`/rezervacija/${this.state.korak+1}`}>
      <Button variant="outlined" size="large" 
        style={nextStyle}
        onClick={this.next}>
        
        {this.state.proba}
        
      </Button>
      </Link>
    </div>
  </div>
    </Fragment>
    </div></div> 
  )
            }
}
export default withRouter(StepOne);
